mapper.insert:
 	
	 
		SELECT NVL(MAX(m_no) + 1, 1) from member
	 	
		INSERT INTO
			member(
				m_no, m_id, m_pw, m_name, m_tel, 
				m_addr, m_mail, m_birth
			)
		VALUES(
			#{mno}, #{mid}, #{mpw}, #{mname}, #{mtel},
			#{maddr}, #{mail}, #{mbirth}
		)

	
